import React from "react";
import "../styles/Map.css";

const locations = {
  "A Coruña": { x: 120, y: 50 },
  "Vigo": { x: 70, y: 200 },
  "Pontevedra": { x: 90, y: 180 },
  "Ourense": { x: 140, y: 230 },
  "Lugo": { x: 150, y: 80 },
  "Santiago de Compostela": { x: 130, y: 120 }
};

const MapComponent = ({ fullAlertData = [], filters }) => {
  // 🔹 Filtrar alertas según los filtros activos
  const filteredAlerts = fullAlertData.filter(alert => {
    const alertDate = new Date(alert.Fecha_hora);
    const alertMonth = alertDate.toLocaleString("en-US", { month: "short" });

    return (
      (!filters.ubicacion || filters.ubicacion === "Todas" || alert.Ubicación === filters.ubicacion) &&
      (!filters.estado || filters.estado === "Todas" || alert.Estado === filters.estado) &&
      (!filters.tipo || filters.tipo === "Todos" || alert.Tipo === filters.tipo) &&
      (!filters.mes || alertMonth === filters.mes)
    );
});

  console.log("📍 Ubicaciones activas en el mapa:", filteredAlerts.map(alert => alert.Ubicación));

  return (
    <div className="map-container">
      <img src="/Galicia.png" alt="Mapa de Galicia" className="map-image" />

      {filteredAlerts.map((alert, index) => (
        locations[alert.Ubicación] && (
          <div
            key={index}
            className="map-point"
            style={{
              left: `${(locations[alert.Ubicación].x / 500) * 100}%`,
              top: `${(locations[alert.Ubicación].y / 400) * 100}%`,
            }}
          ></div>
        )
      ))}
    </div>
  );
};

export default MapComponent;
