import React, { useState } from "react";
import "../styles/Filters.css";

const Filters = ({ onFilterChange }) => {
    const currentMonth = new Date().toLocaleString("es-ES", { month: "long" }); // 游댳 Obtener el mes actual en espa침ol

    const [estado, setEstado] = useState("Activas");
    const [tipo, setTipo] = useState("Todos");
    const [ubicacion, setUbicacion] = useState("Todas");
    const [mes, setMes] = useState(currentMonth); // 游댳 Inicializar con el mes actual

    const handleEstadoChange = (event) => {
        setEstado(event.target.value);
        onFilterChange({ estado: event.target.value, tipo, ubicacion, mes });
    };

    const handleTipoChange = (event) => {
        setTipo(event.target.value);
        onFilterChange({ estado, tipo: event.target.value, ubicacion, mes });
    };

    const handleUbicacionChange = (event) => {
        setUbicacion(event.target.value);
        onFilterChange({ estado, tipo, ubicacion: event.target.value, mes });
    };

    const handleMesChange = (event) => {
        setMes(event.target.value);
        onFilterChange({ estado, tipo, ubicacion, mes: event.target.value });
    };

    return (
        <div className="filters">
            <label>Estado:</label>
            <select value={estado} onChange={handleEstadoChange}>
                <option value="Activas">Activas</option>
                <option value="Pendiente">Pendiente</option>
                <option value="En revisi칩n">En revisi칩n</option>
                <option value="Arreglada">Arreglada</option>
                <option value="Todas">Todas</option>
            </select>

            <label>Tipo:</label>
            <select value={tipo} onChange={handleTipoChange}>
                <option value="Todos">Todos</option>
                <option value="Soplante">Soplante</option>
                <option value="Compresor">Compresor</option>
                <option value="Generador">Generador</option>
                <option value="Motor">Motor</option>
                <option value="Bomba">Bomba</option>
            </select>

            <label>Ubicaci칩n:</label>
            <select value={ubicacion} onChange={handleUbicacionChange}>
                <option value="Todas">Todas</option>
                <option value="Vigo">Vigo</option>
                <option value="A Coru침a">A Coru침a</option>
                <option value="Santiago de Compostela">Santiago de Compostela</option>
                <option value="Lugo">Lugo</option>
                <option value="Ourense">Ourense</option>
            </select>

            <label>Mes:</label>
            <select value={mes} onChange={handleMesChange}>
                {["enero", "febrero", "marzo", "abril", "mayo", "junio", "julio", "agosto", "septiembre", "octubre", "noviembre", "diciembre"]
                .map(m => <option key={m} value={m}>{m.charAt(0).toUpperCase() + m.slice(1)}</option>)}
            </select>
        </div>
    );
};

export default Filters;
