import { Icon } from "leaflet";

// üîß Icono personalizado
const customIcon = new Icon({
  iconUrl: "https://leafletjs.com/examples/custom-icons/leaf-red.png",
  iconSize: [25, 41],
  iconAnchor: [12, 41],
});

// üìç Componente separado para el mapa
const MapComponent = ({ fullAlertData }) => {
  const [isClient, setIsClient] = useState(false);

  useEffect(() => {
    if (typeof window !== "undefined") setIsClient(true);
  }, []);

  if (!isClient) return <p>Cargando mapa...</p>; // ‚úÖ Evita ejecuci√≥n en SSR

  return (
    <MapContainer center={[42.875, -8.544]} zoom={8} style={{ height: "400px", width: "100%" }}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
      {fullAlertData.map((alert, index) => (
        alert.lat && alert.lng && (
          <Marker key={index} position={[alert.lat, alert.lng]} icon={customIcon}>
            <Popup>
              <strong>{alert.M√°quina}</strong><br />
              {alert.Tipo_aver√≠a}
            </Popup>
          </Marker>
        )
      ))}
    </MapContainer>
  );
};

export default MapComponent;
